pragma solidity ^0.5.4;

import "./SafeMath.sol";
import "./Entitys.sol";
/***
    
 */
contract SingleTokenGame{

    modifier onlyOwner {
    require(
        msg.sender == _owner,
        "Only owner can call this function."
    );
    _;
    }

    using SafeMath for uint256;
    

    /** */
    uint256 private _fee_rate=10;

    /** */
    //assert(1 wei == 1);
    //assert(1 szabo == 1e12);
    //assert(1 finney == 1e15);
    //assert(1 ether == 1e18);
    uint256 private _decimals=6;
    uint256 private _price_upper_decimals=18;

     /** */
    uint256 private _recommend_rate=10;
    address private _owner;

    /** */
    uint256 private _bonus_rate=30;
    /**
        
     */
    Entitys.GameStruct private  current_game;

    //
    address[] private   game_buy_users;


    /***
    ->->
    */
    mapping(address=>Entitys.UserBenefitAndInvestment) private game_investment_benefit;
    /**
    ->->
     */
    mapping(address=>uint)  private   game_user_buy_count;


    //
    mapping(address=>uint256) private  user_credit_log;

    constructor (uint _game_id,string memory _name,uint _game_times,uint _price,uint _price_upper_rate) public 
    {
        _owner=msg.sender;
        uint temp_now=now;
        uint _last_buy_time=temp_now;
        uint _over_time=temp_now.add(_game_times);
        uint _buy_price=_price*(10**uint(_decimals));//1^6
        uint256 price_upper_rate=_price_upper_rate*(10**_price_upper_decimals);//1^18
        uint next_change_rate_count=70;
        current_game = Entitys.GameStruct(_game_id,_name,temp_now,_game_times,_last_buy_time,_over_time,_buy_price,_buy_price,price_upper_rate,price_upper_rate,Entitys.GameStatus.Ing,0,0,_owner,next_change_rate_count);

    }
    /**
        
        
    */
    function getNow() public view returns (uint nowvalue)  
    {
        return now;
    }
     /***
     *  
     * */
    function getDecimals()  public view  returns(uint256)
    {
        return _decimals;
    }
    
     /***
     *  
     * */
    function getOwner()  public view  returns(address)
    {
        return _owner;
    }
    

    /**
    * 
     */
    function getCurrentRoundInfo() public view returns (uint game_id,string memory name,uint begin_time,uint game_times,uint last_buy_time,uint over_time,uint256 price
    ,uint price_upper_rate,Entitys.GameStatus status,uint256 prize_value,uint buy_count
    ,address last_buy_user_address,uint next_change_rate_count)
    {
         Entitys.GameStruct memory game=current_game;
         game_id=game.game_id;
         name=game.name;
         begin_time=game.begin_time;
         game_times=game.game_times;
         last_buy_time=game.last_buy_time;
         over_time=game.over_time;
         price=game.price;
         price_upper_rate=game.price_upper_rate;
         status=game.status;
         prize_value=game.prize_value;
         buy_count=game.buy_count;
         last_buy_user_address=game.last_buy_user_address;
         next_change_rate_count=game.next_change_rate_count;
         return ( game_id,  name, begin_time, game_times, last_buy_time, over_time, price, price_upper_rate,status,prize_value,buy_count,last_buy_user_address,next_change_rate_count);
    }




    function askprice() public view returns (uint256 price) {
        Entitys.GameStruct memory game=current_game;
        return game.price;
    }

    //
    event SendRecommend(address bidder, uint256 amount,string event_msg);
    //
    event SendPlatformFee(address bidder, uint256 amount,string event_msg);
    //
    event CalcBonus(address bidder, uint256 amount,string event_msg);
    
    //
    event TransferAmount(address bidder, uint256 amount,string event_msg);

    //
    event BuyGameEvent(address bidder, uint256 amount,uint256 buy_time,string event_msg);
    /**
    *@recommend_user 
    * token
    *1TRX           ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====1000000"
    *0.1TRX         ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====100000"
    *0.000001TRX    ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====1"
    */
    function buy_token(address recommend_user) public payable returns (bool success){
     
        // grab time
        uint256 _now = now;
        Entitys.GameStruct memory game=current_game;
        require(game.game_id!=uint(0), "game not exist");
        //1 TRX           ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====1000000"
        //0.1 TRX         ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====100000"
        //0.000001 TRX    ="TJBHq5um63GPdmmjhmSppuep69bygK7cJE====1"
        uint256 amount=msg.value;//
        address buyer=msg.sender;//
        uint256 current_price=game.price;//
        uint current_buy_count=game.buy_count;//
        if(current_buy_count==uint(0))
        {
            current_buy_count=1;
        }
        require(game.status == Entitys.GameStatus.Ing,"This Game Not Ing");
        //
        require(current_price  <= amount ,"This Game Buy Price is not limit");
        emit BuyGameEvent(buyer,amount,_now,"");
        //
        uint256 recommend_value=amount.mul(_recommend_rate).div(100) ;

        //
        uint256 platform_value=amount.mul(_fee_rate).div(100) ;
        //
        uint256 bonus_value=amount.mul(_bonus_rate).div(100) ;
        //
        address temp_recommend_user=_owner;//
        //--/
        Entitys.UserBenefitAndInvestment memory bi =game_investment_benefit[buyer]; 
      
        temp_recommend_user=recommend_user;//
        bi.recommend_user_address=recommend_user;//
        
        if(bi.isValue&&bi.recommend_user_address!=address(0))
        {
            temp_recommend_user=bi.recommend_user_address;
        }
    
        for(uint i = 0; i < game_buy_users.length; i++) {
            address temp_user=game_buy_users[i];
            delete user_credit_log[temp_user];
        }
        address(uint160(_owner)).transfer(platform_value);//
        emit SendPlatformFee(_owner,platform_value,"");
        address(uint160(temp_recommend_user)).transfer(recommend_value);//
        emit SendRecommend(temp_recommend_user,recommend_value,"");
        uint256 used_bonus_value=uint256(0);
        // 
        for(uint i = 0; i < game_buy_users.length; i++) {
            address temp_user=game_buy_users[i];
            uint user_buy_count=game_user_buy_count[temp_user];
            //=*/
            uint256 temp_bonus_value=bonus_value.mul(user_buy_count).div(current_buy_count);
            //emit CalcBonus(temp_user,temp_bonus_value,"");
            used_bonus_value=used_bonus_value.add(temp_bonus_value);
            user_credit_log[temp_user]=user_credit_log[temp_user].add(temp_bonus_value);
        }
        //
        //=---
        uint256 temp_prize_value=amount.sub(recommend_value);
        temp_prize_value= temp_prize_value.sub(platform_value);
        temp_prize_value=temp_prize_value.sub(used_bonus_value);

        if(bi.isValue)
        {
            bi =game_investment_benefit[buyer];
            bi.recommend_user_address=temp_recommend_user;
        }else{
            //-
            bi = Entitys.UserBenefitAndInvestment(0,0,temp_recommend_user,true);
            //
            game_buy_users.push(buyer);
        }
        bi.touru=bi.touru.add(amount);
        //
        game_investment_benefit[buyer]=bi;
        for(uint i = 0; i < game_buy_users.length; i++) {
            address temp_user=game_buy_users[i];
            uint256 t_amount=user_credit_log[temp_user];//
            //RTX To User
            if(t_amount>0)
            {
                //
                Entitys.UserBenefitAndInvestment memory temp_bi =game_investment_benefit[temp_user];
                if(temp_bi.isValue)
                {
                    temp_bi.shouyi=temp_bi.shouyi.add(t_amount);
                }
                else{
                    //-
                    temp_bi = Entitys.UserBenefitAndInvestment(t_amount,0,address(0),true);
                }
                game_investment_benefit[temp_user]=temp_bi;
                address(uint160(temp_user)).transfer(t_amount);
                emit TransferAmount(temp_user,t_amount,"");
            }
        }
        for(uint i = 0; i < game_buy_users.length; i++) {
            address temp_user=game_buy_users[i];
            delete user_credit_log[temp_user];
        }
        //+1
        game_user_buy_count[buyer]=game_user_buy_count[buyer].add(1);//+1
        //
        calcGameRate(buyer,temp_prize_value);
        return true;
    }
    /**
    
     */
    function calcGameRate(address buyer,uint256 prize_value) private returns(bool)
    {

         // grab time
        uint256 _now = now;
        Entitys.GameStruct memory game=current_game;
        //
        current_game.last_buy_user_address=buyer;
        //
        current_game.prize_value=game.prize_value.add(prize_value);//=+
        
        //
        uint current_buy_count=game.buy_count.add(1);
        current_game.buy_count=current_buy_count;//+1
        //
        current_game.last_buy_time=_now;//
        //
        current_game.over_time=_now.add(game.game_times);//
        //70-140-280-540-1080-2196-->
        if(current_buy_count==game.next_change_rate_count)
        {
          uint256 temp_price_rate=game.price_upper_rate.div(2);//
          game.price_upper_rate=temp_price_rate;
          //
          game.next_change_rate_count=game.next_change_rate_count.mul(2);
          current_game.price_upper_rate=temp_price_rate;
          //
          current_game.next_change_rate_count=game.next_change_rate_count;
        }
        //
        uint256 _price_upper_rate=game.price_upper_rate.div(10**_price_upper_decimals).add(100);
        uint256 current_price=game.price;//
        //=*115/100
        current_game.price=current_price.mul(_price_upper_rate).div(100);
    }
    /***
     *  
     * */
    function getBuyerBuyCount(address buyer) public view returns(uint)
    {
        return game_user_buy_count[buyer];
    }

    /**
     
     */
    function getBuerIncomeInvestment(address buyer)  public view returns (uint256 income,uint256 investment,address recommend_user_address)
    {
      Entitys.UserBenefitAndInvestment memory bi =game_investment_benefit[buyer]; 
      income=bi.touru;
      investment=bi.shouyi;
      recommend_user_address=bi.recommend_user_address;
      return (income,investment,recommend_user_address);
    }

     function checkGameAndPrize()  public view onlyOwner returns (bool)
     {
        require(_owner ==msg.sender,"Must Owner Execute");
        uint256 _now = now;
        Entitys.GameStruct memory game=current_game;
        if(game.over_time<=_now)
        {
            return true;
        }
        return false;
     }
    function PrizeGame()  public onlyOwner returns (bool)
    {
        require(_owner ==msg.sender,"Must Owner Execute");
         // grab time
        uint256 _now = now;
        Entitys.GameStruct memory game=current_game;
        if(game.over_time<=_now)
        {
            //
            if(game.last_buy_user_address!=address(0))
            {
                //
                address(uint160(game.last_buy_user_address)).transfer(game.prize_value);//
                uint temp_now=now;
                uint _last_buy_time=temp_now;
                uint _over_time=temp_now.add(game.game_times);
                uint _buy_price=game.base_prce;//1^6
                uint256 price_upper_rate=game.base_price_upper_rate;//1^18
                uint next_change_rate_count=70;
                uint game_id=game.game_id;
                current_game = Entitys.GameStruct(game_id,game.name,temp_now,game.game_times,_last_buy_time,_over_time,_buy_price,_buy_price,price_upper_rate,price_upper_rate,Entitys.GameStatus.Ing,0,0,_owner,next_change_rate_count);
                return true;
            }
            
        }

        return false;
    } 

     function kill() public onlyOwner returns(bool){
       if (_owner == msg.sender) {
            // 
          Entitys.GameStruct memory game=current_game;
          if(game.prize_value>0)
          {
            address(uint160(_owner)).transfer(game.prize_value);
            selfdestruct(msg.sender);
            return true;
          }
       }
       return false;
    }

}

